 * - Gunakan promise untuk handle asynchronous.
 */
 const formatUser = (format) => {
    return new Promise((resolve, reject) => {
      setTimeout(() => {
        const formattedUsers = users.map((user) => `${format} ${user.name}`);
        resolve(formattedUsers);
      }, 3000);
    });
  };
  
  

 /**
