 const findByName = (username) => {
    return new Promise((resolve, reject) => {
        setTimeout(() => {
            const findedUser = users.find((user) =>  user.name === username);
            resolve(findedUser);
        }, 2000);
    })
 };
 
 /**
  * SARAN TODO3 - TODO5.
